DROP VIEW IF EXISTS `v_worktime`;
CREATE ALGORITHM = UNDEFINED DEFINER = `root`@`localhost` SQL SECURITY DEFINER VIEW `v_worktime` AS select `a`.`worker` AS `worker`,`b`.`name` AS `worker_name`,`a`.`task_time` AS `task_time`,`a`.`user_id` AS `user_id`,(`a`.`basic_wage` * `a`.`task_time`) AS `wage`,date_format(`a`.`work_date`,'%Y-%m-%d') AS `work_date`,date_format(`a`.`work_date`,'%Y-%m') AS `work_month`,date_format(`a`.`work_date`,'%Y') AS `work_year` from (`worktime` `a` left join `worker` `b` on((`a`.`worker` = `b`.`id`))) where ((`a`.`del_flag` = 0) or (`a`.`del_flag` is null));

